INSERT INTO "public"."attachments" ("id", "expediture_type", "installment", "attachments") VALUES ('1', 'ΔΚΑ ΑΝΑΚΑΤΑΣΚΕΥΗ', 'Α', '"{\"Αίτηση του/των ιδιοκτήτη/συνιδιοκτητών.\",\"Έκθεση Αυτοψίας που εκδόθηκε από την Υπηρεσία.\",\"Τίτλους ιδιοκτησίας του ακινήτου (εις διπλούν) με το τοπογραφικό διάγραμμα που τους συνοδεύει.\",\"Πρόσφατο πιστοποιητικό ιδιοκτησίας από Υποθηκοφυλακείο ή κτηματολογικό φύλλο.\",\"Υ/Δ του ν.1599/1986 (εις διπλούν) με κείμενο: α)Δεν έχω πάρει άλλο άτοκο δάνειο... β)Δεν έχω πάρει δωρεάν κρατική αρωγή... γ)Επιθυμώ η δωρεάν κρατική αρωγή... δ)Τα στοιχεία του/της ιδιοκτήτη/-τριας....συμβόλαιο.\",\"Στις περιπτώσεις μεταβίβασης μετά την ημερομηνία του συμβάντος Υ/Δ του ν.1599/1986 (εις διπλούν) του/της προκατόχου του ακινήτου με κείμενο: Δεν έχω πάρει άλλο άτοκο δάνειο από άλλο φορέα....από το προαναφερόμενο συμβάν.\",\"Οικοδομική άδεια του πληγέντος κτιρίου. Σε περίπτωση που δεν υπάρχει, προσκόμιση βεβαίωσης από το Δασαρχείο ή την αρμόδια Υ.ΔΟΜ. ότι δε βρίσκεται σε δασική έκταση.\",\"Φωτοαντίγραφο ΑΔΤ του/των ιδιοκτήτη/συνιδιοκτητών.\"]}'), ('2', 'ΔΚΑ ΑΝΑΚΑΤΑΣΚΕΥΗ', 'Β', null), ('3', 'ΔΚΑ ΑΝΑΚΑΤΑΣΚΕΥΗ', 'Γ', null), ('4', 'ΔΚΑ ΑΝΑΚΑΤΑΣΚΕΥΗ', 'Δ', null);